# Helper function for month conversion
def get_month_number(month_input):
    """Convert month name or number to month number (1-12)"""
    if month_input is None:
        return None
    
    # If it's already a number
    try:
        month_num = int(month_input)
        if 1 <= month_num <= 12:
            return month_num
    except ValueError:
        pass
    
    # Month name mapping
    month_mapping = {
        'january': 1, 'jan': 1,
        'february': 2, 'feb': 2,
        'march': 3, 'mar': 3,
        'april': 4, 'apr': 4,
        'may': 5,
        'june': 6, 'jun': 6,
        'july': 7, 'jul': 7,
        'august': 8, 'aug': 8,
        'september': 9, 'sep': 9, 'sept': 9,
        'october': 10, 'oct': 10,
        'november': 11, 'nov': 11,
        'december': 12, 'dec': 12
    }
    
    return month_mapping.get(month_input.lower())
